:root{
  --color-1-a            : #0C0E11;
  --color-1-b            : #0C0E11;
  --text-color           : #D0EBEE;
  --border-color         : #52677C;

  --foloder-color        : #0C0E11;
  --folder-border        : #52677C;
  --folder-text-color    : #D0EBEE;
}

#main {
  //margin-left: 30px;
  margin-left: auto;
  margin-right: auto;
  padding-left: 30px;
}

#main a { 
  position     : relative;
  margin-bottom: 15px;
  box-sizing   : border-box;
  min-width    : 140px;
  max-width    : 140px;
  transition   : all 0.2s ease-in-out !important;
  background: linear-gradient(to right, var(--color-1-a), var(--color-1-b));
  color: var(--text-color);
  border: solid;
  border-width: 1px;
  border-color: var(--border-color);
}

#main a .icon {
  filter: grayscale(60%);
}

li a::before{
  position  : absolute;
  content   : "";
  top       : 0;
  right     : 0;
  bottom    : 0;
  left      : 0;
  opacity   : 0;
  z-index   : -1;
  transition: opacity 0.3s linear;
}

a:hover::before{
  opacity: 1;
}

#main li a:hover{
 transform : scale(1.1) translateY(-5px);
}

.folder{
  background: var(--folder-color) !important;
  border: solid;
  border-width: 1px;
  border-color: var(--folder-border);
  color: var(--folder-text-color);
}

