options.css:
:root{
  --color-1-a     : #52677C;
  --color-1-b     : #42475A;
  --color-2-a     : #52677C;
  --color-2-b     : #42475A;
  --color-3-a     : #52677C;
  --color-3-b     : #42475A;
  --color-4-a     : #52677C;
  --color-4-b     : #42475A;
  --color-5-a     : #52677C;
  --color-5-b     : #42475A;
  --foloder-color : #28282f
}

#main a { 
  position     : relative;
  margin-bottom: 15px;
  box-sizing   : border-box;
  min-width    : 120px;
  max-width    : 120px;
  transition   : all 0.2s ease-in-out !important;
}

li a::before{
  position  : absolute;
  content   : "";
  top       : 0;
  right     : 0;
  bottom    : 0;
  left      : 0;
  opacity   : 0;
  z-index   : -1;
  transition: opacity 0.3s linear;
}

a:hover::before{
  opacity: 1;
}

#main li a:hover{
 transform : scale(1.1) translateY(-5px);
}


#main a .icon{
  filter: drop-shadow(0px 4px 6px rgba(0,0,0,0.5)) opacity(80%);
}

#main li:nth-of-type(5n) a{
  background: linear-gradient(to right, var(--color-1-a), var(--color-1-b));
}

#main li:nth-of-type(5n+1) a{
  background: linear-gradient(to right, var(--color-2-a), var(--color-2-b));
}

#main li:nth-of-type(5n+2) a{
  background: linear-gradient(to right, var(--color-3-a), var(--color-3-b));
}

#main li:nth-of-type(5n+3) a{
  background: linear-gradient(to right, var(--color-4-a), var(--color-4-b));
}

#main li:nth-of-type(5n+4) a{
  background: linear-gradient(to right, var(--color-5-a), var(--color-5-b));
}

.folder{
  background: var(--foloder-color) !important;
}

#main .column {
  margin-right: 0px;
}
